void testbench::end_of_simulation()
{
   if (!_checked_results) {
      SC_REPORT_INFO(name(), "Simulation ran into deadlock");
      check_results();
   }
}
// ============================================
// Function: check_results
// --------------------------------------------

void testbench::check_results()
{
   for (std::vector<mc_end_of_testbench*>::iterator i = _end_of_tb_objs.begin(); i != _end_of_tb_objs.end(); ++i)
      (*i)->end_of_testbench();
   
   if (_checked_results) return;
   _checked_results = true;
   cout<<endl;
   cout<<"Checking results"<<endl;
   _failed = false;
   if (main_exit_code) _failed = true;
   int _num_outputs_checked = 0;
   
   if (!_capture_image_out) {
      cout<<"'image_out' - warning, output was optimized away"<<endl;
   } else {
      _num_outputs_checked++;
      cout<<"'image_out'"<<endl;
      cout<<"   capture count        = "<<image_out_capture_count<<endl;
      cout<<"   comparison count     = "<<image_out_comp->get_compare_count();
      if (image_out_comp->get_partial_compare_count()) 
         cout <<" ("<<image_out_comp->get_partial_compare_count()<<" partial)";
      if (image_out_comp->get_mask_compare_count()) 
         cout <<" ("<<image_out_comp->get_mask_compare_count()<<" masked)";
      cout << endl;
      cout<<"   ignore count         = "<<image_out_comp->get_ignore_count()<<endl;
      cout<<"   error count          = "<<image_out_comp->get_error_count()<<endl;
      cout<<"   stuck in dut fifo    = "<<ccs_image_out->used()<<endl;
      cout<<"   stuck in golden fifo = "<<image_out_golden.used()<<endl;
      if (image_out_comp->get_error_count() > 0) cout << "   Error: output 'image_out' had comparison errors"<<endl;
      if (image_out_comp->get_compare_count() < image_out_capture_count) cout << "   Error: output 'image_out' has incomplete comparisons"<<endl;
      if (image_out_skip_noerr) {
         if (image_out_capture_count == 0) cout << "   Warning: output 'image_out' has no golden values to compare against"<<endl;
      } else
         if (image_out_capture_count == 0) cout << "   Error: output 'image_out' has no golden values to compare against"<<endl;
      _failed = _failed || image_out_comp->get_error_count() > 0;
      _failed = _failed || image_out_comp->get_compare_count() < image_out_capture_count;
      if (!image_out_skip_noerr)
         _failed = _failed || image_out_capture_count == 0;
      cout<<endl;
   }
   cout<<endl;
   if (_num_outputs_checked == 0) {
      cout<<"Error: All outputs were optimized away. No output values were compared."<<endl;
      _failed = _failed || (_num_outputs_checked == 0);
   }
   if (main_exit_code) cout << "Error: C++ Testbench 'main()' returned a non-zero exit code ("<<main_exit_code<<"). Check your testbench." <<endl;
   cout<<(_failed ? "Error: ":"Info: ")<<"Simulation "<<(_failed ? "FAILED":"PASSED")<<" @ "<<sc_time_stamp()<<endl;
}
