// Process: SC_THREAD run
// Static sensitivity: 

void testbench::run() {
   image_in_ignore = false;
   image_in_skip = false;
   image_in_skip_quiet = false;
   image_in_skip_once = false;
   image_in_skip_noerr = false;
   image_in_array_comp_first = -1;
   image_in_array_comp_last = -1;
   image_in_wait_cycles = 0;
   image_in_wait_ctrl.clear();
   image_in_capture_count = 0;
   image_in_iteration_count = 0;
   image_out_ignore = false;
   image_out_skip = false;
   image_out_skip_quiet = false;
   image_out_skip_once = false;
   image_out_skip_noerr = false;
   image_out_array_comp_first = -1;
   image_out_array_comp_last = -1;
   image_out_use_mask = false;
   image_out_output_mask = ~0;
   image_out_wait_cycles = 0;
   image_out_wait_ctrl.clear();
   image_out_capture_count = 0;
   image_out_iteration_count = 0;
   main_exit_code = main();
   cout<<"Info: Execution of user-supplied C++ testbench 'main()' has completed with exit code = " << main_exit_code << endl;
   cout<<endl;
   cout<<"Info: Collecting data completed"<<endl;
   cout<<"   captured "<<image_in_capture_count<<" values of image_in"<<endl;
   cout<<"   captured "<<image_out_capture_count<<" values of image_out"<<endl;
   testbench_ended = true;
   testbench_end_event.notify(SC_ZERO_TIME);
}
